internal void ProcessAuthentication(SignInStatus status) {
    AuthenticationManager.OnLoginStart?.Invoke();
    if (status == SignInStatus.Success) {
        Debug.Log("Successfully authenticated with Google Play Games.");
        string displayname = PlayGamesPlatform.Instance.GetUserDisplayName();
        string id = PlayGamesPlatform.Instance.localUser.id;
        UserRegisterRequestModel userModel = new() {
            Id = id,
            Type = PlayerPrefNameString.GOOGLE,
            Name = displayname,
        };
        var jsonSettings = new JsonSerializerSettings {
            DefaultValueHandling = DefaultValueHandling.Ignore
        };
        string jsonData = JsonConvert.SerializeObject(userModel, jsonSettings);
        APIList aPIList = new();
        WebRequests.Post(aPIList.GetSocialLoginEndpoint(), jsonData, OnSuccessSocialLogin, OnErrorSocialLogin);
    } else {
        AuthenticationManager.OnLoginFailed?.Invoke("Failed to authenticate with Google Play Games.");
        Debug.Log("Failed to authenticate with Google Play Games.");
    }
}
